import React from 'react'
import logo from '../assets/k-r8-logo.png'


function LoginForm({ isVisible, onClose }) {
    if (!isVisible) return null;
    const handleClose = (e) => {
        if(e.target.id === 'wrapper') onClose()
    }

    return (
        <main>
            <div className='fixed w-full inset-0 bg-black bg-opacity-25 backdrop-blur-sm flex justify-evenly items-center' id='wrapper' onClick={() => onClose()}>
                <div className='w-[50%] flex flex-col'>
                    <button className='w-20 text-white text-xl place-self-end hover:scale-150 transition-all' onClick={() => onClose()}>X</button>
                    <div className='bg-white p-2 mx-8 rounded'>
                        Login Form
                    </div>
                </div>
                <div className='hidden relative w-[50%] lg:flex h-full items-center justify-center bg-white'>
                    <img src={logo} className='w-60 h-60 bg-gradient-to-tr animate-bounce duration-1000 ease-in-out direction-alternate'></img>
                </div>
                <div className='w-[50%] h-[50%] hidden lg:flex absolute right-0 bottom-0 bg-black/10 backdrop-blur-lg '></div>
            </div>
        </main>
    )
}

export default LoginForm
