import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react'
import 'swiper/css'
import 'swiper/css/pagination'
import 'swiper/css/free-mode'

import { FreeMode, Pagination } from 'swiper/modules'

import { RxArrowTopRight } from 'react-icons/rx'
import { ServiceData } from '../constants'




function HeroSlider() {

    return (
        <div className='flex items-center justify-center flex-col'>
            <div className='flex items-center justify-center flex-col my-5'>
                <h1 className='font-semibold text-4xl'>Staff</h1>
            </div>
            <Swiper
                breakpoints={{
                    200:{
                        slidesPerView: 2,
                        spaceBetween: 15
                    },
                    340: {
                        slidesPerView: 2,
                        spaceBetween: 15
                    },
                    500: {
                        slidesPerView: 2,
                        spaceBetween: 15
                    },
                    700: {
                        slidesPerView: 3,
                        spaceBetween: 15
                    },
                    900: {
                        slidesPerView: 4,
                        spaceBetween: 15
                    }
                }}

                freeMode={true}
                pagination={{
                    clickable: true
                }}
                modules={[FreeMode, Pagination]}
                className='w-full' 
            >
                {ServiceData.map((item) => (
                    <SwiperSlide key={item.title}>
                        <div className='flex flex-col gap-6 mb-20 group relative shadow-lg text-white rounded-xl px-6 py-8 h-[250px] w-[180px]  md:h-[250px] md:w-[215px]'>
                            <div className='absolute inset-0 bg-cover bg-center rounded-xl' 
                            style={{backgroundImage: `url(${item.backgroundImage})`}}>
                            </div>
                            <div className='absolute inset-0 bg-transparent group-hover:opacity-50'></div>
                            <div className='relative flex flex-col gap-3'>
                                <item.icon className='text-secondarylight group-hover:text-blue-400 w-[32px] h-[32px]' />
                                <h1 className='text-xl lg-text-2xl'>{item.title}</h1>
                                <p className='lg:text-[18px]'>{item.content}</p>
                            </div>
                            <RxArrowTopRight className='absolute bottom-5 left-5 w-[35px] h-[35px] text-white group-hover:text-blue-500 group-hover:rotate-45 duration-100'></RxArrowTopRight>
                        </div>
                    </SwiperSlide>
                ))}
            </Swiper>

        </div>
    )
}

export default HeroSlider
