import { useState, useEffect } from 'react'


function Carousel({ children: slide,
    autoSlide = false,
    autoSlideInterval = 3000
}) {
    const [curr, setCurr] = useState(0)

    const prev = () => setCurr((curr) => (curr == 0 ? slide.length - 1 : curr - 1))
    const next = () => setCurr((curr) => (curr == slide.length - 1 ? 0 : curr + 1))

    useEffect(() => {
        if (!autoSlide) return
        const slideInterval = setInterval(next, autoSlideInterval)
        return () => clearInterval(slideInterval)
    }, [])

    return (
        <div className='overflow-hidden relative'>
            <div className='flex transition-transform ease-out duration-500'
                style={{ transform: `translateX(-${curr * 100}%)` }}>
                {slide}
            </div>
            <div className='absolute inset-0 flex items-center justify-between p-4'>
                <button className='p-1 rounded-full shadow bg-white-80 text-gray-800 hover:bg-white'
                    onClick={prev}>
                    <i className='bx bxs-chevrons-left'
                        size={40} />
                </button>
                <button className='p-1 rounded-full shadow bg-white-80 text-gray-800 hover:bg-white'
                    onClick={next}>
                    <i className='bx bxs-chevrons-right' />
                </button>
            </div>

            <div className='absolute bottom-4 right-0 left-0'>
                <div className='flex items-center justify-center gap-2'>
                    {slide.map((_, i) => (
                        <div className={`
                        transition-all w-3 h-3 bg-white rounded-full ${curr == i ? 'p-2' : 'bg-opacity-50'}
                        `}
                        />
                    ))}
                </div>
            </div>

        </div>
    )
}

export default Carousel
