import React from 'react'
import { Swiper, SwiperSlide } from 'swiper/react'
import 'swiper/css'
import 'swiper/css/pagination'
import 'swiper/css/free-mode'

import { FreeMode, Pagination } from 'swiper/modules'

import { RxArrowTopRight } from 'react-icons/rx'
import { ServiceData } from '../constants'




function HeroSlider() {

    return (
        <div className='flex items-center justify-center flex-col h-screen bg-slate-200'>
            <Swiper
                breakpoints={{
                    340: {
                        slidesPerView: 2,
                        spaceBetween: 15
                    },
                    700: {
                        slidesPerView: 3,
                        spaceBetween: 15
                    }
                }}

                freeMode={true}
                pagination={{
                    clickable: true
                }}
                modules={[FreeMode, Pagination]}
                className='max-w-[90$] lg:max-w-[80%]'
            >
                {ServiceData.map((item) => (
                    <SwiperSlide key={item.title}>
                        <div className='flex flex-col gap-6 group relative shadow-lg text-white rounded-xl px-6 py-8 h-[350px] w-[215px] lg:h-[400px] lg:w-[350px]'>
                            <div className='absolute inset-0 bg-cover bg-center' 
                            style={{backgroundImage: `url(${item.backgroundImage})`}}>
                            </div>
                            <div className='absolute inset-0 bg-black opacity-10 group-hover:opacity-50'></div>
                        </div>
                    </SwiperSlide>
                ))}
            </Swiper>

        </div>
    )
}

export default HeroSlider
